Bash(/Users/newmba/security/monitoring/grafana/dashboards/overview.json << 'EOF'
{
  "dashboard": {
    "title": "Security Triage System - Overview",
    "tags": ["security", "triage", "overview"],
    "timezone": "browser",
    "schemaVersion": 16,
    "version": 0,
    "refresh": "30s",
    "panels": [
      {
        "id": 1,
        "title": "Alert Processing Rate",
        "type": "graph",
        "gridPos": {"h": 8, "w": 12, "x": 0, "y": 0},
        "targets": [
          {
            "expr": "rate\(http_requests_total{job=\\"alert-ingestor\\"}[1m]\)",
            "legendFormat": "{{{{ job }}}",
            "refId": "A"
          }
        ]
      },
      {
        "id": 2,
        "title": "AI Triage Duration \(P95\)",
        "type": "graph",
        "gridPos": {"h": 8, "w": 12, "x": 12, "y": 0},
        "targets": [
          {
            "expr": "histogram_quantile\(0.95, rate\(ai_triage_duration_seconds_bucket[5m]\)\)",
            "legendFormat": "P95",
            "refId": "A"
          }
        ]
      },
      {
        "id": 3,
        "title": "Database Connections",
        "type": "gauge",
        "gridPos": {"h": 8, "w": 6, "x": 0, "y": 8},
        "targets": [
          {
            "expr": "pg_stat_database_numbackends{datname=\\"security_triage\\"}",
            "refId": "A"
          }
        ]
      },
      {
        "id": 4,
        "title": "Redis Memory Usage",
        "type": "gauge",
        "gridPos": {"h": 8, "w": 6, "x": 6, "y": 8},
        "targets": [
          {
            "expr": "redis_memory_used_bytes / redis_memory_max_bytes * 100",
            "refId": "A"
          }
        ]
      },
      {
        "id": 5,
        "title": "RabbitMQ Queue Depth",
        "type": "graph",
        "gridPos": {"h": 8, "w": 12, "x": 12, "y": 8},
        "targets": [
          {
            "expr": "rabbitmq_queue_messages{queue=\\"alert.ingest\\"}",
            "legendFormat": "{{{{ queue }}}",
            "refId": "A"
          }
        ]
      }
    ]
  }
}
EOF)
